/*Table structure for table `userinfo` */

DROP TABLE IF EXISTS `userinfo`;

CREATE TABLE `userinfo` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户名id',
  `user_name` varchar(20) NOT NULL COMMENT '用户名',
  `user_pwd` varchar(50) NOT NULL COMMENT '用户名密码',
  `user_salt` varchar(10) NOT NULL COMMENT '盐值',
  `user_reallyName` varchar(20) DEFAULT NULL COMMENT '真实名字',
  `user_sex` varchar(1) DEFAULT NULL COMMENT '性别',
  `user_email` varchar(100) DEFAULT NULL COMMENT '邮箱',
  `user_qq` varchar(20) DEFAULT NULL COMMENT 'QQ',
  `user_tel` varchar(20) DEFAULT NULL COMMENT '电话',
  `user_address` varchar(100) DEFAULT NULL COMMENT '地址',
  `user_path` varchar(200) DEFAULT NULL COMMENT '头像地址',
  PRIMARY KEY (`user_id`),
  UNIQUE KEY `user_name` (`user_name`),
  KEY `user_email` (`user_email`),
  KEY `user_email_2` (`user_email`)
) ENGINE=InnoDB AUTO_INCREMENT=10 DEFAULT CHARSET=utf8;

